mapper.select:
SELECT * FROM ( SELECT row_number() over(order by NOTICE_DATE DESC, ID DESC) as RNO , ID , TITLE , CONTENT , TB_NOTICE.C_ID , TB_COMPANY.NAME , NOTICE_HITS , CAST( DATE_FORMAT( NOTICE_DATE, '%Y-%m-%d %H:%i:%s' ) AS CHAR(19) ) AS NOTICE_DATE FROM TB_NOTICE INNER JOIN TB_COMPANY ON TB_NOTICE.C_ID = TB_COMPANY.COMPANY_ID WHERE
TITLE LIKE CONCAT('%',#{keyword},'%') OR CONTENT LIKE CONCAT('%',#{keyword},'%')
TITLE LIKE CONCAT('%',#{keyword},'%')
CONTENT LIKE CONCAT('%',#{keyword},'%')
TB_COMPANY.NAME LIKE CONCAT('%',#{keyword},'%')
) T1 WHERE RNO between #{pageMap.startCount} AND #{pageMap.endCount}

